<style scoped>.label {    display: inline-block;    width: 300px;}.label__button {    text-align: center;    border: 1px solid #eee;    height: 50px;    line-height: 50px;    display: block;}#input { display: none }.upload {    margin: 20px 0;    padding: 20px 0;}.upload__image {    width: 200px;    height: auto;}.loading {    background: #efefef;    font-size: 30px;    width: 300px;    height: 300px;    text-align: center;    line-height: 300px;}</style><template>    <div id='upload'>        <label for='input' class='label'>            <span class='label__button'>点击上传图片, 生成hash</span>            <input type='file'                   id='input'                   @change='handleUploadImage'            />        </label>        <div class='upload' v-show='!loading && src' >            <img :src='src' alt='upload-image' class='upload__image'>        </div>        <div v-show='loading' class='loading'>encode...</div>    </div></template><script>export default {    data: () => ({        src: '',        loading: false,    }),    methods: {        async handleUploadImage(e) {            if (!e.target.files.length) return;            const fs = e.target.files[0];            if (!fs.type.includes('image')) return;            this.loading = true;            const fileReader = new FileReader()            fileReader.onload = this.base64ImageToBlurHash;            fileReader.readAsDataURL(fs);        },        async base64ImageToBlurHash(e) {            const base64 = e.target.result;            const params = new FormData();            params.append('avatar', base64);            console.log(JSON.stringify(params))            const options = { headers: { 'Content-Type': 'multipart/form-data' } }            const r = await this.request({                url: '/uploader',                method: 'POST',                data: params,                options,            })            console.log('r ? => ', r)        }    },};</script>