<template>    <div id='detail' v-if='!!imageData'>        <blurHashImage v-bind='imageData' :image-src='imageSrc' />    </div></template><script>import blurHashImage from './../components/blur-hash-image';export default {    components: {        blurHashImage,    },    data: () => ({        imageData: null,        imageSrc: ''    }),    async mounted() {        await this.getBlurHashData();    },    methods: {        async getBlurHashData() {            const id = this.$route.query.id;            const {                blurHash: {                    hash,                    width: imageWidth,                    height: imageHeight,                }            } = await this.request({ url: `/blur.hash?id=${id}` });            this.imageData = {                hash,                imageWidth,                imageHeight,                designWidth: imageWidth,                designHeight: imageHeight,                punch: 1            }            setTimeout(() => {                this.imageSrc = 'https://i.ibb.co/N6SHSBh/4391614406243-pic-thumb.jpg';            }, 4000)        },    },};</script>