<style scoped></style><template>    <div id='example'><!--        <imageUploadHash @encode='handleEncodeImageFinish'/>-->        <imageUploadStyle @encode='handleEncodeImageFinish'/>        <router-link            v-if='id'            tag='h3'            :to="routerLinkTo"        >预览</router-link>    </div></template><script>import blurHashImage from './../components/blur-hash-image'import imageUploadHash from './../components/image-upload-hash'import imageUploadStyle from './../components/image-upload-style'export default {    data: () => ({        id: null,    }),    components: {        blurHashImage,        imageUploadHash,        imageUploadStyle,    },    computed: {        routerLinkTo() {            return { path: 'detail', query: { id: this.id }}        }    },    methods: {        async handleEncodeImageFinish({ id, hash, width, height }) {            await this.request({                method: 'POST',                url: '/upload',                data: {                    id, hash, width, height                }            })            this.id = id;        }    }};</script>